import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { Cabinet } from 'src/app/shared/models/cabinet';
import { CabinetService } from 'src/app/shared/services/cabinet.service';
import { AppToastService } from 'src/app/shared/utils/AppToast.service';
import { RoleManager } from 'src/app/shared/utils/role-manager';
import { Utilisateur } from 'src/app/shared/models/utilisateur';
import { AppConfig } from 'src/app/shared/utils/app-config';
import { UtilisateurService } from 'src/app/shared/services/utilisateur.service';
import { environment } from 'src/environments/environment';
import { SearchParam } from 'src/app/shared/utils/search-param';
import * as SecureLS from 'secure-ls';
import { Modal } from 'bootstrap';
@Component({
  selector: 'app-cabinet',
  templateUrl: './cabinet.component.html',
  styleUrls: ['./cabinet.component.css']
})
export class CabinetComponent implements OnInit {
  public cabinet: Cabinet;
  public cabinets: Cabinet[] = [];
  public selectedCabinets: Cabinet[]
  public utilisateurs: Utilisateur[] = [];
  public userFilter = { prenom: '' };
  public roleManager: RoleManager;
  public searchParam: SearchParam;
  public isSearch: boolean = false;
  public currentUser: Utilisateur;
  public searchFilterText: string = '';
  public loading: boolean;
  public isDark: boolean;
  public criteriaList = [
    { id: 1, name: 'N° du cabinet' },
    { id: 2, name: 'Nom du cabinet' },
    { id: 3, name: 'Nom d\'organisation' },
  ];
  @ViewChild('closeAddElementDialog') closeAddElementDialog: any;
  @ViewChild('openConfirmDialog') openConfirmDialog: any;
 @ViewChild('deleteConfirmDialog', { static: false }) deleteConfirmDialog!: ElementRef;
 private deleteModal?: Modal;
 paginatedList: any[] = []; // la portion affichée
  itemsPerPage = 10; // nombre d’éléments par page
  totalPages = 0;
  selectAll: boolean = false;
  public currentView = 'list';
  public currentPage = 1; 
  constructor(
    private cabinetService: CabinetService,
    private utilisateurService: UtilisateurService,
    private toast: AppToastService,
    private appConfig: AppConfig
  ) {
    this.currentUser = this.appConfig.currentUser;
    this.roleManager = new RoleManager();
    this.searchParam = new SearchParam();
  }

  ngOnInit(): void {
    const ls = new SecureLS({ encodingType: 'aes', encryptionSecret: 'MyAdminApp' });
    if (ls.get('current_theme')) {//dark
      this.isDark = true;
      const headerLeft = document.getElementsByClassName("theme-light");
      for(var i = headerLeft.length - 1; i >= 0; --i) {
        headerLeft[i].classList.replace('theme-light', 'theme-dark');
      }
      this.changePaginationBg('#000000');
      this.changePaginationFg('#ffffff');
      this.changePrimeTbBg('#000000');
      this.changePrimeTbHead('#000000');
      this.changePaginatorLight('#252116');
      this.changeRowPad('rgb(21 21 21 / 100%)');
      this.changeTrHover('#1c1c1c');
      this.changeBgsearchbar('rgb(28, 28, 28)');
      this.changeBgBtnSave('#f78e39');
      this.changeBgTheader('rgb(37 33 22)');
    } else {//white
      this.isDark = false;
      const headerLeft = document.getElementsByClassName("theme-dark");
      for(var i = headerLeft.length - 1; i >= 0; --i) {
        headerLeft[i].classList.replace('theme-dark', 'theme-light');
      }
      this.changePaginationBg('#ffffff');
      this.changePaginationFg('#000000');
      this.changePrimeTbBg('none');
      this.changePrimeTbHead('#f8f9fa');
      this.changePaginatorLight('#ecf5ee');
      this.changeRowPad('rgb(255 255 255 / 100%)');
      this.changeTrHover('rgba(0, 0, 0, 0.3)');
      this.changeBgsearchbar('rgba(255, 255, 255, 0.8)');
      this.changeBgBtnSave('#605bff');
      this.changeBgTheader('rgba(255, 199, 154, 0.5)');
    }
    this.searchParam.dateFin.setDate(this.searchParam.dateFin.getDate() + 1);
    this.Search();
  this.cabinets.map(item => ({
    ...item,
    selected: false
  }));

this.totalPages = Math.ceil(this.cabinets.length / this.itemsPerPage);
    this.updatePaginatedList();
  }
 updatePaginatedList() {
    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    this.paginatedList = this.cabinets.slice(startIndex, endIndex);
  }

  nextPage() {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
      this.updatePaginatedList();
    }
  }

  previousPage() {
    if (this.currentPage > 1) {
      this.currentPage--;
      this.updatePaginatedList();
    }
  }
  toggleSelectAll() {
  this.selectAll = !this.selectAll;
  this.cabinets.forEach(item => item.selected = this.selectAll);
}

toggleSelectOne() {
  // si tous sont cochés, selectAll = true sinon false
  this.selectAll = this.cabinets.every(item => item.selected);
}
  changeTrHover(newValue: string): void {
    document.documentElement.style.setProperty('--tr-hover', newValue);
  }
  changePaginationBg(newValue: string): void {
    document.documentElement.style.setProperty('--bgpaginator', newValue);
  }
  changePaginationFg(newValue: string): void {
    document.documentElement.style.setProperty('--fgpaginator', newValue);
  }
  changePrimeTbBg(newValue: string): void {
    document.documentElement.style.setProperty('--bgprimetb', newValue);
  }
  changePrimeTbHead(newValue: string): void {
    document.documentElement.style.setProperty('--bgprimetbhead', newValue);
  }
  changePaginatorLight(newValue: string): void {
    document.documentElement.style.setProperty('--paginatorlight', newValue);
  }
  changeRowPad(newValue: string): void {
    document.documentElement.style.setProperty('--bg-trtable', newValue);
  }
  changeBgsearchbar(newValue: string): void {
    document.documentElement.style.setProperty('--bg-searchbar', newValue);
  }
  changeBgBtnSave(newValue: string): void {
    document.documentElement.style.setProperty('--btn-save-bg', newValue);
  }
  changeBgTheader(newValue: string): void {
    document.documentElement.style.setProperty('--theaderbg', newValue);
  }
  cabinetFiltres () {
    if (!this.searchFilterText) return this.cabinets
    const search = this.searchFilterText.toLowerCase()
    return this.cabinets.filter(item => {
      const text = (item.id + ' ' + item.designation).toLowerCase()
      return text.indexOf(search) > -1
    })
  }
  showList() {
     this.currentView = 'list';
   }
  showAddForm() {
    this.cabinet = new Cabinet();
    this.currentView = 'add';
  }
  showEditForm(cabinet: Cabinet) {
    this.cabinet = cabinet;
    this.currentView = 'add';
  }
  showConfirmDialog(cabinet: Cabinet): void {
    this.cabinet = cabinet;
    this.openConfirmDialog.nativeElement.click();
  }

  removePersonnel(perso: Utilisateur) {
    this.cabinet.personnels.forEach((elmt, index) => {
      if (elmt.id === perso.id) {
        this.cabinet.personnels.splice(index, 1);
      }
    });
  }

  Search() {
    this.loading = true;
    this.cabinetService.findAll().subscribe(ret => {
      if (ret['code'] === 200) {
        this.cabinets = ret['data'];
        console.log("cabinets===>", this.cabinets);
        this.loading = false;
        this.toast.info(this.cabinets.length+ " cabinet(s) trouvé(s)");
      } else {
        this.loading = false;
        this.toast.error(ret['message']);
      }
    }, error => {
      console.log("error===>", error);
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }
  Save() {
    this.cabinet.statut = "Activé";
   // this.cabinet.nombregreffier = 0;
    this.loading = true;
    this.cabinetService.save(this.cabinet).subscribe(ret => {
      if (ret['code'] === 200) {
        this.cabinet = ret['data'];
        this.cabinets.push(this.cabinet)
        this.loading = false;
        this.closeAddElementDialog.nativeElement.click();
        this.toast.success("Cabinet enregistré avec succès");
      } else {
        this.loading = false;
        this.toast.error(ret['message']);
      }
    }, error => {
      console.log("error===>", error);
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }
  Update() {
    this.loading = true;
    this.cabinetService.update(this.cabinet).subscribe(ret => {
      if (ret['code'] === 200) {
        this.cabinet = ret['data'];
        this.cabinets.push(this.cabinet);
        this.cabinets.forEach(cab => {
          if (cab.id === this.cabinet.id) {
            cab = this.cabinet;
          }
        });
        this.loading = false;
        this.closeAddElementDialog.nativeElement.click();
        this.toast.success("Cabinet modifié avec succès");
      } else {
        this.loading = false;
        this.toast.error(ret['message']);
      }
    }, error => {
      console.log("error===>", error);
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }
 UpdateStatut(statut: string) {
    this.loading = true;
    this.cabinet.statut = statut;

    this.cabinetService.updateStatut(this.cabinet).subscribe({
      next: (ret) => {
        this.loading = false;
        if (ret['code'] === 200) {
          this.cabinet = ret['data'];
          this.cabinets.forEach(c => {
            if (c.id === this.cabinet.id) c.statut = this.cabinet.statut;
          });

          // ✅ Si statut = Bloqué, ouvrir le modal de confirmation
          if (statut === 'Bloqué') {
            const modalEl = document.getElementById('blockConfirmModal');
            if (modalEl) {
              const modal = new Modal(modalEl);
              modal.show();
            }
          }

          this.toast.success(ret['message']);
        } else {
          this.toast.error(ret['message']);
        }
      },
      error: () => {
        this.loading = false;
        this.toast.error(environment.erreur_connexion_message);
      }
    });
  }
  ngAfterViewInit(): void {
      // Initialise le modal après que le DOM soit prêt
      const modalEl = document.getElementById('deleteModal');
      if (modalEl) {
        this.deleteModal = new Modal(modalEl, { backdrop: 'static', keyboard: false });
      } else {
        console.error("⚠️ Élément #deleteModal introuvable dans le DOM.");
      }
    }

    showDeleteDialog(cabinet: Cabinet): void {
       this.cabinet = cabinet;
      if (this.deleteModal) {
        this.deleteModal.show();
      } else {
        console.warn("⚠️ Le modal n’a pas été initialisé.");
      }
    }
    closeDeleteDialog(): void {
      this.deleteModal?.hide();
    }
     onDeleted(): void {
     this.loading = true;
     this.cabinetService.delete(this.cabinet).subscribe((ret:any) => {
      if (ret['code'] == 200) {
        this.cabinet = ret['data'];
         //this.openConfirmDialog.nativeElement.click();
         this.closeDeleteDialog()
         this.Search();
         this.loading = false;
         this.toast.success(ret['message']);
       } else {
         this.loading = false;
         this.toast.error(ret['message']);
       }
     }, error => {
       console.log("error====>", error);
       this.loading = false;
       this.toast.error('Une erreur est survenue lors de l\'opération');
     });
   }

  initUsers() {
    this.utilisateurService.findAll().subscribe((ret) => {
      if (ret['code'] === 200) {
        this.utilisateurs = ret['data'];
        this.loading = false;
      } else {
        this.toast.error(ret['message']);
        this.loading = false;
      }
      },
      () => {
        this.toast.error('Problème de connexion');
        this.loading = false;
      }
    );
  }
}
<ngx-loading [show]="loading" [config]="{ backdropBorderRadius: '5px',fullScreenBackdrop:true }"></ngx-loading>
<div class="row">
  <div class="col-md-12">
    <!-- list -->
    <div [class]="isDark? 'tile theme-dark' : 'tile theme-light'">
       <div class="user-header d-flex justify-content-between align-items-center p-3 shadow-sm rounded-3">
        <!-- 🔹 Titre -->
        <div class="d-flex align-items-center gap-2">
          <h5 class="fw-bold mb-0 text-dark">
            Liste des cabinets
          </h5>
          <span class="badge-count">{{cabinets.length}}</span>
        </div>
        <!-- 🔹 Outils : recherche + boutons -->
        <div class="d-flex align-items-center gap-2 flex-wrap justify-content-end">
          <!-- 🔍 Barre de recherche -->
          <div class="search-wrapper d-flex align-items-center">
            <i class="mdi mdi-magnify search-icon"></i>
            <input type="text" [(ngModel)]="searchFilterText" placeholder="Recherche" class="search-input" />
          </div>
          <!-- 🧩 Bouton filtre -->
          <button *ngIf="!isSearch" (click)="isSearch = true"
            class="btn btn-outline-light filter-btn d-flex align-items-center gap-1">
            <i class="mdi mdi-filter-variant text-dark"></i>
            <span class="text-dark">Filtres</span>
          </button>
          <div class="row gx-1 align-items-center" *ngIf="isSearch">
            <div class="col-auto d-flex align-items-center">
              <label class="lab_input me-1" [class]="isDark? 'c-white' : 'c-black'">Du</label>
              <p-calendar [(ngModel)]="searchParam.dateDebut" [showIcon]="true"></p-calendar>
            </div>

            <div class="col-auto d-flex align-items-center">
              <label class="lab_input me-1" [class]="isDark? 'c-white' : 'c-black'">Au</label>
              <p-calendar [(ngModel)]="searchParam.dateFin" [showIcon]="true"></p-calendar>
            </div>

            <div class="col-auto">
              <p-dropdown [options]="criteriaList" [(ngModel)]="searchParam.criteria" optionLabel="name"
                placeholder="Critère"></p-dropdown>
            </div>

            <div class="col-auto">
              <button class="btn_refresh" (click)="Search()" title="Rafraichir">
                <i class="mdi mdi-refresh fs-20"></i>
              </button>
            </div>

            <div class="col-auto">
              <button class="btn_refresh text-danger" (click)="isSearch = false" title="Fermer">
                <i class="mdi mdi-close fs-20"></i>
              </button>
            </div>
          </div>
          <!-- ➕ Bouton ajout -->
          <button *ngIf="true" class="btn btn-dark d-flex align-items-center gap-1 px-3" (click)="showAddForm()" type="button" data-backdrop="static" data-toggle="modal" data-target="#addCabinetModal">
            <i class="mdi mdi-plus"></i>
            <span>{{'cabinet.addcabinet' | translate}}</span>
          </button>
        </div>
      </div>
      <div class="table-card">
        <div class="table-responsive">
          <table class="table align-middle table-hover table-striped">
            <thead class="table-light">
              <tr>
                <th>
                  <div class="animated-checkbox">
                    <label>
                      <input type="checkbox" [(ngModel)]="selectAll" (change)="toggleSelectAll()" />
                      <span class="label-text"></span>
                    </label>
                  </div>
                </th>
                <th>N°</th>
                <th> Services</th>
               <th> Designation</th>
              <th> Statut </th>
              <th class="text-center action-buttons">Opérations</th>
              </tr>
            </thead>

            <tbody>
              <tr *ngFor="let cabine of cabinets">
                <td>
                  <div class="animated-checkbox">
                    <label>
                      <input type="checkbox" [(ngModel)]="cabine.selected" (change)="toggleSelectOne()" />
                      <span class="label-text"></span>
                    </label>
                  </div>
                </td>
                <td>{{ cabine.id }}</td>
                <td>{{ cabine.designation }}</td>
                <td>{{ cabine.description }}</td>
                <td><span [class]="cabine.statut==='Activé'? 'text-c-green ft-w-500' : 'text-c-red ft-w-500'">{{ cabine.statut }}</span></td>

                <td class="text-center action-buttons">
                  <div class="d-flex justify-content-center align-items-center gap-2 flex-wrap">
                    <button class="btn btn-light btn-icon" title="Modifier" data-backdrop="static" data-toggle="modal"
                      data-target="#addUserModal" (click)="showEditForm(cabinet)">
                      <img src="assets/icons/mdi/edit.png" alt="Modifier" class="icon-btn" />
                    </button>

                    <button class="btn btn-light btn-icon" title="Supprimer" data-backdrop="static" data-toggle="modal"
                      data-target="#deleteConfirmModal" (click)="showDeleteDialog(cabinet)">
                      <img src="assets/icons/mdi/delete.png" alt="Supprimer" class="icon-btn" />
                    </button>
                    <button *ngIf="cabine.statut !== 'Activé'" class="btn btn-primary btn-sm fw-semibold" title="Debloquer" data-backdrop="static"
                      data-toggle="modal" data-target="#userConfirmModal" (click)="showConfirmDialog(cabinet)">Débloquer</button>
                    <button *ngIf="cabine.statut == 'Activé'" class="btn btn-danger btn-sm fw-semibold" title="Bloquer" data-backdrop="static"
                      data-toggle="modal" data-target="#userConfirmModal" (click)="showConfirmDialog(cabinet)">Bloquer</button>
                  </div>
                </td>
              </tr>
            </tbody>
          </table>
        </div>

        <!-- 🔹 PAGINATION -->
        <!-- 🔹 PAGINATION DYNAMIQUE -->
        <div class="pagination-wrapper d-flex justify-content-between align-items-center mt-3 p-2 border-top">
          <div class="d-flex align-items-center gap-2">
            <button class="btn btn-outline-secondary btn-sm" [disabled]="currentPage === 1" (click)="previousPage()">
              <i class="mdi mdi-chevron-left"></i> Précédent
            </button>
            <ng-container *ngFor="let page of [].constructor(totalPages); let i = index">
              <button class="btn btn-sm"
                [ngClass]="currentPage === i + 1 ? 'btn-primary text-white' : 'btn-outline-secondary'"
                (click)="currentPage = i + 1; updatePaginatedList()">
                {{ i + 1 }}
              </button>
            </ng-container>

            <button class="btn btn-outline-secondary btn-sm" [disabled]="currentPage === totalPages"
              (click)="nextPage()">
              Suivant <i class="mdi mdi-chevron-right"></i>
            </button>
          </div>

          <div class="text-muted small">
            Page {{ currentPage }} sur {{ totalPages }}
          </div>
        </div>
      </div>
    </div>
    <!-- /list -->

    <!-- add cabinet modal -->
    <div class="container demo" *ngIf="currentView === 'add'">
      <div class="modal right fade" id="addCabinetModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel2">
        <div class="modal-dialog" role="document">
          <div class="modal-content" [style]="{'background-color': isDark ? '#1f1f1f' : '#FFFFFF'}">
            <div class="d-flex" style="margin-top: 15px;">
              <button type="button" class="close" data-dismiss="modal" aria-label="Close" style="margin-left: 10px;" #closeAddElementDialog>
                <span aria-hidden="true">
                  <i [class]="isDark? 'txt-title-d mdi mdi-arrow-left-bold-circle' : 'mdi mdi-arrow-left-bold-circle'"></i>
                </span>
                <span [class]="isDark? 'txt-title-d ml-2': 'ml-1'" style="font-size: 12px;">
                  {{'cabinet.addcabinet' | translate}}
                </span>
              </button>
            </div>

            <div class="modal-body">
              <div class="row mb-l-20 mb-10">
                <div class="col-md-12 col-lg-12 p-right-0">
                  <div class="row mb-l-20 mb-10">
                    <div class="col-md-12 col-lg-12 p-right-0">
                      <div [class]="isDark? 'card cust_card_d':'card cust_card_w'">
                        <div class="card-block">
                          <div class="row mb-3 mt-2">
                            <div class="col-md-12 col-lg-12 p-right-0 ta-center">
                              <label [class]="isDark? 'form-control-label ft-w-b c-cab-1' : 'form-control-label ft-w-b c-cab-2'">
                                {{'cabinet.infogenerale' | translate}} :
                              </label>
                            </div>
                          </div>
                          <div class="row mb-l20">
<!--                             <div class="col-lg-12 p-right-0">
                              <div class="form-group has-success align-left ft-w-500">
                                <label [class]="isDark? 'form-control-label c-cab-1' : 'form-control-label c-cab-2'" for="numerocabinet">{{'cabinet.numerocabinet' | translate}}</label>
                                <input [class]="isDark? 'form-control form-control-d is-valid' : 'form-control form-control-w is-valid'" id="numerocabinet" name="numerocabinet" type="text" [(ngModel)]="cabinet.numerocabinet" required>
                              </div>
                            </div> -->
                            <div class="col-lg-12 p-right-0">
                              <div class="form-group has-success align-left ft-w-500">
                                <label [class]="isDark? 'form-control-label c-cab-1' : 'form-control-label c-cab-2'" for="nomcabinet">{{'cabinet.nomcabinet' | translate}}</label>
                                <input [class]="isDark? 'form-control form-control-d is-valid' : 'form-control form-control-w is-valid'" id="nomcabinet" name="nomcabinet" type="text" [(ngModel)]="cabinet.designation" required>
                              </div>
                            </div>
                            <div class="col-lg-12 p-right-0">
                              <div class="form-group has-success align-left ft-w-500">
                                <label [class]="isDark? 'form-control-label c-cab-1' : 'form-control-label c-cab-2'" for="description">{{'cabinet.description' | translate}}</label>
<!--                                 <input [class]="isDark? 'form-control form-control-d is-valid' : 'form-control form-control-w is-valid'" id="description" name="description" type="text" [(ngModel)]="cabinet.description" required>
 -->                                <textarea [class]="isDark? 'form-control form-control-d is-valid' : 'form-control form-control-w is-valid'" placeholder="Taper la description ici ..." id="description" name="description" type="text" [(ngModel)]="cabinet.description" rows="10" required></textarea>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                    <div [class]="isDark? 'col-md-12 col-lg-12 p-right-0 div_separate_d':'col-md-12 col-lg-12 p-right-0 div_separate_w'"></div>

                    <div class="col-md-12 col-lg-12 p-right-0" *ngIf="">
                      <div [class]="isDark? 'card cust_card_d':'card cust_card_w'">
                        <div class="card-block">
                          <div class="row mb-3 mt-2">
                            <div class="col-md-12 col-lg-12 p-right-0 ta-center">
                              <label [class]="isDark? 'form-control-label ft-w-b c-cab-1' : 'form-control-label ft-w-b c-cab-2'">
                                {{'cabinet.addpersonne' | translate}} :
                              </label>
                            </div>
                          </div>

                          <div class="row mb-l20">
                            <div class="col-md-12 col-lg-12 p-right-0">
                              <div class="form-group has-success align-left">
                                <mat-form-field appearance="fill" class="full-width select-add">
                                  <mat-label>Veuillez cocher les personnels pour le cabinet</mat-label>
                                  <mat-select name="addpersonne" [(ngModel)]="cabinet.personnels" multiple>
                                    <mat-option *ngFor="let perso of utilisateurs" [value]="perso">{{perso.prenom}} {{perso.nom}}</mat-option>
                                  </mat-select>
                                </mat-form-field>
                              </div>
                            </div>
                          </div>

                          <div class="row">
                            <div class="col-md-11 col-lg-11 cust_row mb-1" *ngFor="let perso of cabinet.personnels; let i=index">
                              <div class="row">
                                <div class="col-md-10 col-lg-10 p-right-0">
                                  <label [class]="isDark? 'form-control-label fs-12 c-cab-1' : 'form-control-label fs-12 c-cab-2'">
                                    <div class="d-flex">
                                      <img src="assets/images/profile/ali_soumahoro.png" alt="" class="avatar_av mr-1 ml-2">
                                      <span> {{perso.prenom}} {{perso.nom}}</span>
                                    </div>
                                  </label>
                                </div>
                                <div class="col-md-2 col-lg-2 d-flex">
                                  <button class="btn btn-remove btn-sm mr-1 h-30" type="button" (click)="removePersonnel(perso)">
                                    <i class="mdi mdi-close fs-18"></i>
                                  </button>
                                </div>
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>

                  </div>
                  <div class="row">
                    <div class="col-md-12">
                      <button class="btn btn-login btn-block btnn_save" (click)="cabinet.id?Update():Save()">
                        <img src="assets/images/save.png" alt="" class="icon_save">{{'parametre.save' | translate}}
                      </button>
                    </div>
                    <div class="col-md-12 ta-center">
                      <label class="lab_reset" *ngIf="!cabinet.id" (click)="showAddForm()">Réinitialiser</label>
                      <label class="lab_reset" *ngIf="cabinet.id" data-dismiss="modal" aria-label="Close">Fermer</label>
                    </div>
                  </div>
