    reader.readAsDataURL(event.target.files[0]);
  }

  /* +++++++++++++++++++++++++ new code for signature++++++++++++++++++ */
   @ViewChild('canvas', { static: true }) canvasRef!: ElementRef<HTMLCanvasElement>;
  private ctx!: CanvasRenderingContext2D;
  private drawing = false;

  ngAfterViewInit() {
    const canvas = this.canvasRef.nativeElement;
    this.ctx = canvas.getContext('2d')!;
    this.ctx.lineWidth = 2;
    this.ctx.strokeStyle = '#000';

    canvas.addEventListener('mousedown', this.startDrawing);
    canvas.addEventListener('mouseup', this.stopDrawing);
    canvas.addEventListener('mouseout', this.stopDrawing);
    canvas.addEventListener('mousemove', this.draw);
  }

  startDrawing = (event: MouseEvent) => {
    this.drawing = true;
    this.ctx.beginPath();
    this.ctx.moveTo(event.offsetX, event.offsetY);
  };

  stopDrawing = () => {
    this.drawing = false;
    this.ctx.closePath();
  };

  draw = (event: MouseEvent) => {
    if (!this.drawing) return;
    this.ctx.lineTo(event.offsetX, event.offsetY);
    this.ctx.stroke();
  };

  saveSignature() {
    const dataURL = this.canvasRef.nativeElement.toDataURL('image/png');
    console.log('Signature sauvegardée : ', dataURL);
    alert('Signature enregistrée avec succès ✅');
  }

 updatePaginatedList() {
    const startIndex = (this.currentPage - 1) * this.itemsPerPage;
    const endIndex = startIndex + this.itemsPerPage;
    this.paginatedList = this.activeList.slice(startIndex, endIndex);
  }

  nextPage() {
    if (this.currentPage < this.totalPages) {
      this.currentPage++;
      this.updatePaginatedList();
    }
  }

  previousPage() {
    if (this.currentPage > 1) {
      this.currentPage--;
      this.updatePaginatedList();
    }
  }

formatPhone(numero: string): string {
  if (!numero) return '';
  // Exemple simple pour Mali
  if (numero.startsWith('+223')) {
    return numero.replace(/(\+223)(\d{2})(\d{2})(\d{2})(\d{2})/, '$1 $2 $3 $4 $5');
  }
  return numero;
}
