import { Component, OnInit } from '@angular/core';
import { TimeagoIntl } from 'ngx-timeago';
import { NotificationObjet } from 'src/app/shared/models/notification';
import { NotifyService } from 'src/app/shared/services/notification.service';
import { AppToastService } from 'src/app/shared/utils/AppToast.service';
import { strings as englishStrings } from 'ngx-timeago/language-strings/en';
import { strings as englishShortStrings } from 'ngx-timeago/language-strings/en-short';
import { strings as frenchStrings } from 'ngx-timeago/language-strings/fr';
import { strings as frenchShortStrings } from 'ngx-timeago/language-strings/fr-short';
import { environment } from 'src/environments/environment';
import { AppConfig } from 'src/app/shared/utils/app-config';
import { PaginationInstance } from 'ngx-pagination';
import * as SecureLS from 'secure-ls';
import { Utilisateur } from 'src/app/shared/models/utilisateur';
@Component({
  selector: 'app-notification',
  templateUrl: './notification.component.html',
  styleUrls: ['./notification.component.css']
})
export class NotificationComponent implements OnInit {

  public loading: boolean;
  public isDark: boolean;
  public currentUser: Utilisateur;
  public notifications1: NotificationObjet[] = [];
  public notifications: any[] = [];
  public live = true;
  public lang = 'fr-short';
  public config: PaginationInstance = {
    id: 'advanced',
    itemsPerPage: 5,
    currentPage: 1
  };
  public labels: any = {
    previousLabel: 'PrÃ©c',
    nextLabel: 'Suiv',
    screenReaderPaginationLabel: 'Pagination',
    screenReaderPageLabel: 'page',
    screenReaderCurrentLabel: `You're on page`
  };
  constructor(
    private toast: AppToastService,
    private notifyService: NotifyService,
    private intl: TimeagoIntl,
    private appConfig: AppConfig) {
    this.currentUser = this.appConfig.currentUser;
  }

  ngOnInit(): void {
    const ls = new SecureLS({ encodingType: 'aes', encryptionSecret: 'MyAdminApp' });
    if (ls.get('current_theme')) {//dark
      this.isDark = true;
      const headerLeft = document.getElementsByClassName("theme-light");
      for(var i = headerLeft.length - 1; i >= 0; --i) {
        headerLeft[i].classList.replace('theme-light', 'theme-dark');
      }
    } else {//white
      this.isDark = false;
      const headerLeft = document.getElementsByClassName("theme-dark");
      for(var i = headerLeft.length - 1; i >= 0; --i) {
        headerLeft[i].classList.replace('theme-dark', 'theme-light');
      }
    }
    this.InitNotifications();
     this.notifications = [
    {
      title: 'Nouvel utilisateur inscrit',
      message: 'Mr Coulibaly vient de crÃ©er un compte sur la plateforme.',
      time: 'Il y a 5 minutes',
      icon: 'mdi-account-plus',
      type: 'success',
      read: false
    },
    {
      title: 'Demande en attente',
      message: 'Une nouvelle demande doit Ãªtre validÃ©e.',
      time: 'Il y a 1 heure',
      icon: 'mdi-clock-outline',
      type: 'warning',
      read: false
    },
    {
      title: 'Erreur de synchronisation',
      message: 'Le serveur nâ€™a pas pu se connecter Ã  la base de donnÃ©es.',
      time: 'Hier',
      icon: 'mdi-alert-circle-outline',
      type: 'danger',
      read: true
    }
  ];


  }

  onPageChange(number: number) {
    this.config.currentPage = number;
  }
  onPageBoundsCorrection(number: number) {
    this.config.currentPage = number;
  }

  InitNotifications() {
    this.notifyService.findByEventUser(this.currentUser.id).subscribe(ret => {
      if (ret['code'] === 200) {
        const events = ret['data'];
        const notifs = ret['Notification'];
        this.notifications = events.concat(notifs);
        console.log('notifs===', this.notifications);
        this.loading = false;
      } else {
        this.loading = false;
        this.toast.error(ret['message']);
      }
    }, error => {
      console.log("error===>", error);
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }
  markAsRead(notif: any) {
    notif.read = true;
  }

  markAllAsRead() {
    this.notifications.forEach(n => n.read = true);
  }
}
<ngx-loading [show]="loading" [config]="{ backdropBorderRadius: '5px',fullScreenBackdrop:true }"></ngx-loading>
<div class="notification-container card shadow-sm p-3">
  <div class="d-flex justify-content-between align-items-center mb-3">
    <h5 class="mb-0 fw-bold">ðŸ”” Notifications</h5>
    <button class="btn btn-sm btn-outline-primary" (click)="markAllAsRead()">
      <i class="mdi mdi-check-all me-1"></i> Marquer tout comme lu
    </button>
  </div>

  <ul class="list-group list-group-flush">
    <li *ngFor="let notif of notifications" 
        class="list-group-item d-flex align-items-start justify-content-between notification-item"
        [ngClass]="{ 'unread': !notif.read }">

      <div class="d-flex align-items-start">
        <div class="icon-circle me-3" [ngClass]="notif.type">
          <i class="mdi" [ngClass]="notif.icon"></i>
        </div>
        <div>
          <h6 class="mb-1">{{ notif.title }}</h6>
          <p class="text-muted small mb-0">{{ notif.message }}</p>
          <small class="text-muted"><i class="mdi mdi-clock-outline"></i> {{ notif.time }}</small>
        </div>
      </div>

      <button class="btn btn-sm btn-light ms-2" (click)="markAsRead(notif)">
        <i class="mdi mdi-eye"></i>
      </button>
    </li>
  </ul>

  <div *ngIf="notifications.length === 0" class="text-center text-muted mt-3">
    <i class="mdi mdi-bell-off-outline fs-4 d-block mb-2"></i>
    Aucune notification
  </div>
</div>
