  initUsers() {
    this.loading = true;
    this.demandeService.findAll().subscribe(ret => {
      if (ret['code'] == 200) {
        const users = ret['data'];
        users.forEach(user => {
          user.nomComplet = user.prenom + ' ' + user.nom;
        });
        if (this.currentUser.service_name === 'siege') {
          this.demandes = users.filter(u => {
            return u.service_name === 'siege';
          });
        } else {
          this.demandes = users.filter(u => {
            return u.service_name === 'parquet';
          });
        }
        const actiList = [];
        const blocList = [];
        const deleList = [];
        this.demandes.forEach(user => {
          switch (user.statut) {
            case 'Activé':
              actiList.push(user);
              break;
            case 'Bloqué':
              blocList.push(user);
              break;
            case 'Supprimé':
              deleList.push(user);
              break;
          }
          switch (user.user_agent_id) {
            /* siege */
            case 2:
              this.nombreAssistante++;
              break;
            case 3:
              this.nombreJuge++;
              break;
            case 4:
              this.nombreGreffier++;
              break;
            /* parquet */
            case 7:
              this.nombreAssistante++;
              break;
            case 8:
              this.nombreJuge++;
              break;
            case 9:
              this.nombreGreffier++;
              break;
            default:
              break;
          }
        });
        this.activeList = actiList;
        this.bloqueList = blocList;
        this.suppriList = deleList;
        this.loading = false;
        this.toast.info(this.demandes.length + " demande(s) trouvé(s)");
      } else {
        this.toast.error(ret['message']);
        this.loading = false;
      }
    }, () => {
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }
  Save() {
    this.demande.statut = "Activé";
    this.currentUser.service_name;
    // this.demande.image = null;
    this.loading = true;
    this.demandeService.save(this.demande).subscribe(ret => {
      if (ret['code'] === 200) {
        this.demande = ret['data'];
        this.demandes.push(this.demande);
        this.closeAddElementDialog.nativeElement.click();
