import { Component, OnInit } from "@angular/core";
import { Router } from "@angular/router";
import { AppToastService } from "../shared/utils/AppToast.service";
import { environment } from "src/environments/environment";
import { AuthService } from "../shared/services/auth.service";
import { AuthUser } from "../shared/models/auth-user";
import { UtilisateurService } from "../shared/services/utilisateur.service";
import { JwtHelperService } from '@auth0/angular-jwt';
import * as SecureLS from "secure-ls";

@Component({
  selector: "app-login",
  templateUrl: "./login.component.html",
  styleUrls: ["./login.component.css"],
})
export class LoginComponent implements OnInit {
  public loading: boolean;
  public hidden: boolean = true;
  public isResetPwd: boolean = false;
  public isWhite: boolean = false;
  public authUser: AuthUser = new AuthUser();
  public helper = new JwtHelperService();

  constructor(
    private router: Router,
    private authService: AuthService,
    private userService: UtilisateurService,
    private toast: AppToastService
  ) { }

  ngOnInit(): void {
   const session = localStorage.getItem('current_session_novus');
if (session) {
  // Si déjà connecté, on reste dans l’espace admin sans forcer dashboard
  this.router.navigate(['/admin']);
}
  }

connecter() {
  this.loading = true;
  this.authService.loginUser(this.authUser).subscribe((response: any) => {
    if (response.data) {
      if (!response.data.token) {
        this.toast.error(response.data);
        this.loading = false;
        return;
      }

      const token = response.data.token;
      localStorage.setItem('session', token);

      const decodedToken = this.helper.decodeToken(token);

      this.userService.findById(decodedToken.user_id).subscribe((ret: any) => {
        this.loading = false;

        if (ret['code'] === 200) {
          const ls = new SecureLS({
            encodingType: 'aes',
            encryptionSecret: 'MyAdminApp',
          });

          const userconnect = ret['data'];

          ls.set('current_theme', this.isWhite);
          ls.set('current_session_novus', JSON.stringify({ user: userconnect, droits: ret['droit_user'] }));

          // ✅ Redirection vers le tableau de bord après login
          this.router.navigate(['/dashboard']);
          this.toast.success(ret['message']);

        } else {
          this.toast.error(ret['message']);
        }
      }, () => {
        this.toast.error(environment.erreur_connexion_message);
        this.loading = false;
      });
    } else {
      this.toast.error(response['message']);
      this.loading = false;
    }
  }, () => {
    this.toast.error('Problème de connexion');
    this.loading = false;
  });
}



  toggleShow() {
    this.hidden = !this.hidden;
  }

  onChangeResetPwd() {
    this.isResetPwd = !this.isResetPwd;
  }
  setThemeWhite() {
    const menuLeft = document.getElementsByClassName("bg-cab-2");
    for(var i = menuLeft.length - 1; i >= 0; --i) {
      menuLeft[i].classList.replace('bg-cab-2', 'bg-cab-1');
    }
    const menuLeftColor = document.getElementsByClassName("c-cab-1");
    for(var i = menuLeftColor.length - 1; i >= 0; --i) {
      menuLeftColor[i].classList.replace('c-cab-1', 'c-cab-2');
    }
    const themeMap = document.getElementsByClassName("theme-dark");
    for(var i = themeMap.length - 1; i >= 0; --i) {
      themeMap[i].classList.replace('theme-dark', 'theme-light');
    }
    const themeChart = document.getElementsByClassName("theme-dark-login");
    for(var i = themeChart.length - 1; i >= 0; --i) {
      themeChart[i].classList.replace('theme-dark-login', 'theme-light-login');
    }
    const libMac = document.getElementsByClassName("c-white");
    for(var i = libMac.length - 1; i >= 0; --i) {
      libMac[i].classList.replace('c-white', 'c-black');
    }
    const libNoir = document.getElementsByClassName("c-blanc");
    for(var i = libNoir.length - 1; i >= 0; --i) {
      libNoir[i].classList.replace('c-blanc', 'c-noir');
    }
    const formInput = document.getElementsByClassName("form-control-d");
    for(var i = formInput.length - 1; i >= 0; --i) {
      formInput[i].classList.replace('form-control-d', 'form-control-w');
    }
    this.isWhite = false;
  }
  setThemeDark() {
    const menuLeft = document.getElementsByClassName("bg-cab-1");
    for(var i = menuLeft.length - 1; i >= 0; --i) {
      menuLeft[i].classList.replace('bg-cab-1', 'bg-cab-2');
    }
    const menuLeftColor = document.getElementsByClassName("c-cab-2");
    for(var i = menuLeftColor.length - 1; i >= 0; --i) {
      menuLeftColor[i].classList.replace('c-cab-2', 'c-cab-1');
    }
    const themeMap = document.getElementsByClassName("theme-light");
    for(var i = themeMap.length - 1; i >= 0; --i) {
      themeMap[i].classList.replace('theme-light', 'theme-dark');
    }
    const themeChart = document.getElementsByClassName("theme-light-login");
    for(var i = themeChart.length - 1; i >= 0; --i) {
      themeChart[i].classList.replace('theme-light-login', 'theme-dark-login');
    }
    const libMac = document.getElementsByClassName("c-black");
    for(var i = libMac.length - 1; i >= 0; --i) {
      libMac[i].classList.replace('c-black', 'c-white');
    }
    const libBlanc = document.getElementsByClassName("c-noir");
    for(var i = libBlanc.length - 1; i >= 0; --i) {
      libBlanc[i].classList.replace('c-noir', 'c-blanc');
    }
    const formInput = document.getElementsByClassName("form-control-w");
    for(var i = formInput.length - 1; i >= 0; --i) {
      formInput[i].classList.replace('form-control-w', 'form-control-d');
    }
    this.isWhite = true;
  }
  /*  google map*/
}

<section class="auth-content">
<!--  <div [class]="isWhite?'menu-left bg-left-2':'menu-left bg-left-1'">
    <img src="assets/images/auth/isolation_mode.png" alt="Isolation Mode" style="width: 40px;margin-top: 20px;"/>
    <img src="assets/images/auth/icon_menu.png" alt="Icon menu" style="width: 40px;margin-top: 60px;"/>
    <div class="flat_bottom">
      <img src="assets/images/auth/icon_etoile.png" alt="Isolation Mode" (click)="(isWhite)?setThemeWhite() : setThemeDark()" style="width: 30px;cursor: pointer;"/><br/>
      <img src="assets/images/auth/flat_fr.png" alt="Icon menu" style="width: 20px;margin-top: 30px;"/>
    </div>
  </div>  -->
      <img *ngIf="!isWhite" src="assets/images/v1.png" alt="Nothing" style="margin-top: 15px; float: left;" width="120px" height="120px"/>
    <img *ngIf="isWhite" src="assets/images/v1.png" alt="Nothing" style="margin-top: -15px;"/>
  <div class="auth-menu bg-cab-1" style="padding-top: 120px;">


    <form (ngSubmit)="connecter()" #loginForm="ngForm" novalidate>
      <div class="row">
        <div class="col-xs-12 col-sm-1 col-md-1"></div>
        <div class="col-xs-10 col-sm-10 col-md-10 align-left">
           <label *ngIf="!isResetPwd"class="txt_detenu"><strong style="font-size: 20px;">Bonjour</strong><br/>  Rebonjour Mr, Mme, veuillez vous connecter</label>
          <h3 *ngIf="isResetPwd" class="fw-bold mb-2">Mot de passe oublié? </h3>
          <p *ngIf="isResetPwd" class="text-muted mb-4">Veuillez remplir ce formulaire attentivement svp.</p>
        </div>
      </div>
      <br/>
      <div class="row" style="margin-bottom: 5px;">
        <div class="col-xs-12 col-sm-1 col-md-1"></div>
        <div class="col-xs-12 col-sm-10 col-md-10">
          <div class="form-group has-success align-left ft-w-b">
            <label class="form-control-label c-cab-2" for="email">Adresse email</label>
            <input class="form-control form-control-w is-valid" id="email" name="email" type="email" [(ngModel)]="authUser.email">
          </div>
        </div>
      </div>
      <div class="row">
        <div class="col-xs-12 col-sm-1 col-md-1"></div>
        <div class="col-xs-12 col-sm-10 col-md-10">
          <div class="form-group has-success align-left ft-w-b" *ngIf="!isResetPwd">
            <label class="form-control-label c-cab-2" for="password">Mot de passe</label>
            <input class="form-control form-control-w is-valid" [type]="hidden ? 'password' : 'text'" id="password" name="password" type="password" [(ngModel)]="authUser.password">
            <span id="toggle-password" class="eye-icon">
              <i [class]="hidden ? 'mdi mdi-eye-outline ft-s17' : 'mdi mdi-eye-off-outline ft-s17'" title="Voir ou cacher mot de passe" (click)="toggleShow()"></i>
            </span>
          </div>

          <div class="form-group has-success align-left ft-w-b" *ngIf="isResetPwd">
            <label class="form-control-label c-cab-1" for="login">Votre login actuel</label>
            <input class="form-control form-control-d is-valid" id="login" name="login" type="text" [(ngModel)]="authUser.password">
          </div>
        </div>
      </div>

      <div class="row" style="margin-bottom: 5px;" *ngIf="!isResetPwd">
        <div class="col-xs-12 col-sm-1 col-md-1"></div>
        <div class="col-xs-12 col-sm-5 col-md-5 align-left">
          <div class="animated-checkbox">
            <label style="font-size: 13px;">
              <input type="checkbox"><span class="label-text c-cab-2">Rester connecté</span>
            </label>
          </div>
        </div>
        <div class="col-xs-12 col-sm-5 col-md-5">
          <p class="semibold-text mb-2">
            <a href="javascript:void(0);" (click)="onChangeResetPwd()" class="c-orange tx-deconone" data-toggle="flip">Mot de passe oublié ?</a>
          </p>
        </div>
      </div>


      <div class="row">
        <div class="col-xs-12 col-sm-1 col-md-1"></div>
        <div class="col-xs-12 col-sm-5 col-md-5" *ngIf="isResetPwd">
          <button class="btn btn-secondary btn-block" (click)="onChangeResetPwd()">
            <i class="fa fa-times" style="font-size: 18px;"></i> RETOUR
          </button>
        </div>
        <div [class]="(isResetPwd)? 'col-xs-12 col-sm-5 col-md-5': 'col-xs-12 col-sm-10 col-md-10'">
          <div class="form-group btn-container">
            <button class="btn btn-login btn-block" [style]="{'color': isWhite? '#1c1c1c' : '#FFFFFF'}" [disabled]="!loginForm.valid">
              <i [class]="(isResetPwd)? 'fa fa-send-o': 'fa fa-sign-in'" style="font-size: 18px;"></i> {{isResetPwd? 'ENVOYER' : 'SE CONNECTER'}}
            </button>
          </div>
        </div>
      </div>

    </form>

     <div class="row" style="margin-top: 30px;">
        <div class="col-xs-12 col-sm-1 col-md-1"></div>
        <div class="col-xs-12 col-sm-5 col-md-5 align-left">
          <div class="animated-checkbox">
            <label style="font-size: 13px;">
              <span class="label-text c-cab-2">Vous n’avez pas un compte</span>
            </label>
          </div>
        </div>
        <div class="col-xs-12 col-sm-5 col-md-5">
          <p class="semibold-text mb-2">
            <a routerLink="/demande-compte" class="c-orange tx-deconone" data-toggle="flip">Créer un compte?</a>
          </p>
        </div>
      </div>
  </div>

  <div class="auth-right theme-dark">
    <img src="assets/images/img-login.png" alt="">
  </div>
</section>
