import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { AppToastService } from 'src/app/shared/utils/AppToast.service';
import { RoleManager } from 'src/app/shared/utils/role-manager';
import { AppConfig } from 'src/app/shared/utils/app-config';
import { environment } from 'src/environments/environment';
import { SearchParam } from 'src/app/shared/utils/search-param';
import { Droit } from 'src/app/shared/models/droit';
import { ProfileService } from 'src/app/shared/services/profil.service';
import * as SecureLS from 'secure-ls';
import { Utilisateur } from 'src/app/shared/models/utilisateur';
import { Modal } from 'bootstrap';
import { Profil } from 'src/app/shared/models/profil';
@Component({
  selector: 'app-profil',
  templateUrl: './profil.component.html',
  styleUrls: ['./profil.component.css']
})
export class ProfilComponent implements OnInit {
  public profil: Profil;
  public profils: Profil[] = [];
  public selectedProfils: Profil[]
  public droits: Droit[] = [];
  public droitFilter: any = { nom: '' }
  public roleManager: RoleManager;
  public searchParam: SearchParam;
  public isSearch: boolean = false;
  public currentUser: Utilisateur;
  public currentPage: any = 'list';
  public dialogAction: string;
  public searchFilterText: string = '';
  public loading: boolean;
  public isDark: boolean;
  public isSelectedRole: boolean;
  public criteriaList = [
    { id: 1, name: 'N° ID' },
    { id: 2, name: 'Nom' },
  ];
  @ViewChild('closeAddElementDialog') closeAddElementDialog: any;
  @ViewChild('openConfirmDialog') openConfirmDialog: any;
  @ViewChild('openDeleteDialog') openDeleteDialog: ElementRef;
  private deleteModal?: Modal;
  constructor(
    private profilService: ProfileService,
    private toast: AppToastService,
    private appConfig: AppConfig
  ) {
    this.currentUser = this.appConfig.currentUser;
    this.roleManager = new RoleManager();
    this.searchParam = new SearchParam();
  }

  ngOnInit(): void {
    const ls = new SecureLS({ encodingType: 'aes', encryptionSecret: 'MyAdminApp' });
    if (ls.get('current_theme')) {//dark
      this.isDark = true;
      const headerLeft = document.getElementsByClassName("theme-light");
      for(var i = headerLeft.length - 1; i >= 0; --i) {
        headerLeft[i].classList.replace('theme-light', 'theme-dark');
      }
      this.changePaginationBg('#000000');
      this.changePaginationFg('#ffffff');
      this.changePrimeTbBg('#000000');
      this.changePrimeTbHead('#000000');
      this.changePaginatorLight('#252116');
      this.changeRowPad('rgb(21 21 21 / 100%)');
      this.changeTrHover('#1c1c1c');
      this.changeBgsearchbar('rgb(28, 28, 28)');
      this.changeBgBtnSave('#f78e39');
      this.changeBgTheader('rgb(37 33 22)');
    } else {//white
      this.isDark = false;
      const headerLeft = document.getElementsByClassName("theme-dark");
      for(var i = headerLeft.length - 1; i >= 0; --i) {
        headerLeft[i].classList.replace('theme-dark', 'theme-light');
      }
      this.changePaginationBg('#ffffff');
      this.changePaginationFg('#000000');
      this.changePrimeTbBg('none');
      this.changePrimeTbHead('#f8f9fa');
      this.changePaginatorLight('#ecf5ee');
      this.changeRowPad('rgb(255 255 255 / 100%)');
      this.changeTrHover('rgba(0, 0, 0, 0.3)');
      this.changeBgsearchbar('rgba(255, 255, 255, 0.8)');
      this.changeBgBtnSave('#605bff');
      this.changeBgTheader('rgba(255, 199, 154, 0.5)');
    }
    this.searchParam.dateFin.setDate(this.searchParam.dateFin.getDate() + 1);
    this.Search();
  }
  changeTrHover(newValue: string): void {
    document.documentElement.style.setProperty('--tr-hover', newValue);
  }
  changePaginationBg(newValue: string): void {
    document.documentElement.style.setProperty('--bgpaginator', newValue);
  }
  changePaginationFg(newValue: string): void {
    document.documentElement.style.setProperty('--fgpaginator', newValue);
  }
  changePrimeTbBg(newValue: string): void {
    document.documentElement.style.setProperty('--bgprimetb', newValue);
  }
  changePrimeTbHead(newValue: string): void {
    document.documentElement.style.setProperty('--bgprimetbhead', newValue);
  }
  changePaginatorLight(newValue: string): void {
    document.documentElement.style.setProperty('--paginatorlight', newValue);
  }
  changeRowPad(newValue: string): void {
    document.documentElement.style.setProperty('--bg-trtable', newValue);
  }
  changeBgsearchbar(newValue: string): void {
    document.documentElement.style.setProperty('--bg-searchbar', newValue);
  }
  changeBgBtnSave(newValue: string): void {
    document.documentElement.style.setProperty('--btn-save-bg', newValue);
  }
  changeBgTheader(newValue: string): void {
    document.documentElement.style.setProperty('--theaderbg', newValue);
  }
  profilFiltres () {
    if (!this.searchFilterText) return this.profils
    const search = this.searchFilterText.toLowerCase()
    return this.profils.filter(item => {
      const text = (item.id + ' ' + item.nom).toLowerCase()
      return text.indexOf(search) > -1
    })
  }

  showAddForm() {
    this.profil = new Profil();
    this.initDroits();
    this.currentPage = 'add';
  }

  showEditForm(profil: Profil) {
    this.profil = profil;
    this.currentPage = 'add';
    this.profilService.findDroitByProfilId(this.profil.id).subscribe(ret => {
      if (ret['code'] === 200) {
        this.droits = ret['Droits'];
       // console.log("droits===>", this.droits);
        this.droits.forEach(d => { d.checked = true });
        const otherRights = ret['TousDroits'];
        otherRights.forEach((orth: any) => {
          const droit = this.droits.find((d)=> d.id === orth.id);
          if (droit === undefined) {
            this.droits.push(orth);
          }
        });
        this.loading = false;
      } else {
        this.toast.error(ret['message']);
        this.loading = false;
      }
    }, error => {
      this.toast.info(environment.erreur_connexion_message);
      this.loading = false;
    });
  }
  showConfirmDialog(profil: Profil, action: string): void {
    this.profil = profil;
    this.dialogAction = action;
    this.openConfirmDialog.nativeElement.click();
  }

ngAfterViewInit(): void {
    // Initialise le modal après que le DOM soit prêt
    const modalEl = document.getElementById('deleteModal');
    if (modalEl) {
      this.deleteModal = new Modal(modalEl, { backdrop: 'static', keyboard: false });
    } else {
      console.error("⚠️ Élément #deleteModal introuvable dans le DOM.");
    }
  }

  showDeleteDialog(profil: Profil): void {
    this.profil = profil;
    if (this.deleteModal) {
      this.deleteModal.show();
    } else {
      console.warn("⚠️ Le modal n’a pas été initialisé.");
    }
  }
  closeDeleteDialog(): void {
    this.deleteModal?.hide();
  }
DeleteProfile() {
  this.loading = true;
    this.profilService.delete(this.profil).subscribe(ret => {
      if (ret['code'] === 200) {
        this.profil = ret['data'];
        this.profils.filter(pr => { return pr.id !== this.profil.id});
        this.closeDeleteDialog();
        this.toast.success("Profil supprimé avec succès");
        this.loading = false;
      } else {
        this.toast.error(ret['message']);
        this.loading = false;
      }
    }, error => {
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
}
  Search() {
    this.loading = true;
    this.profilService.findAll().subscribe(ret => {
      if (ret['code'] === 200) {
        this.profils = ret['data'];
        this.loading = false;
      } else {
        this.loading = false;
        this.toast.error(ret['message']);
      }
    }, error => {
      console.log("error===>", error);
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }

  Save() {
    if (this.droits) {
      this.profil.droits = []
      this.droits.forEach(d => {
        if (d.checked !== undefined && d.checked) {
          this.profil.droits.push(d)
        }
      })
    }
    if (this.profil.droits.length === 0) {
      this.toast.warning('Veuillez selectionner au moins 1 droit');
      return
    }
    this.loading = true;
    this.profilService.save(this.profil).subscribe(ret => {
      if (ret['code'] === 200) {
        this.profil = ret['data'];
        this.profils.push(this.profil);
        this.closeAddElementDialog.nativeElement.click();
        this.loading = false;
        this.toast.success("Profil enregistré avec succès");
      } else {
        this.loading = false;
        this.toast.error(ret['message']);
      }
      this.loading = false;
    }, error => {
      console.log("error===>", error);
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }

  Update() {
    if (this.droits) {
      this.profil.droits = []
      this.droits.forEach(d => {
        if (d.checked !== undefined && d.checked) {
          this.profil.droits.push(d)
        }
      })
    }
    if (this.profil.droits.length === 0) {
      this.toast.warning('Veuillez selectionner au moins 1 droit');
      return
    }
    this.loading = true;
    this.profilService.update(this.profil).subscribe(ret => {
      if (ret['code'] === 200) {
        this.profil = ret['data'];
        this.profils.forEach(prof => {
          if (prof.id === this.profil.id) {
            prof = this.profil;
          }
        });
        this.closeAddElementDialog.nativeElement.click();
        this.loading = false;
        this.toast.success("Profil modifié avec succès");
      } else {
        this.loading = false;
        this.toast.error(ret['message']);
      }
      this.loading = false;
    }, error => {
      console.log("error===>", error);
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }
  UpdateStatut(statut: string) {
    this.loading = true;
    this.profil.statut;
    this.profilService.update(this.profil).subscribe(ret => {
      if (ret['code'] === 200) {
        this.profil = ret['data'];
        this.profils.forEach(user => {
          if(user.id === this.profil.id){
            user.statut = this.profil.statut;
          }
        });
        this.openConfirmDialog.nativeElement.click();
        this.toast.success("Profil statut changé avec succès");
        this.loading = false;
      } else {
        this.toast.error(ret['message']);
        this.loading = false;
      }
    }, error => {
      this.toast.error(environment.erreur_connexion_message);
      this.loading = false;
    });
  }

  initDroits() {
    this.profilService.findAllDroit().subscribe((ret) => {
      if (ret['code'] === 200) {
        this.droits = ret['data'];
        this.loading = false;
      } else {
        this.toast.error(ret['message']);
        this.loading = false;
      }
      },
      () => {
        this.toast.error('Problème de connexion');
        this.loading = false;
      }
    );
  }
  // check or uncheck roles
  checkedAllRole() {
    this.droits.forEach(r => {
      r.checked = this.isSelectedRole;
    });
  }
}
<ngx-loading [show]="loading" [config]="{ backdropBorderRadius: '5px',fullScreenBackdrop:true }"></ngx-loading>
<div class="row">
  <div class="col-md-12">
    <!-- list -->
    <div [class]="isDark ? 'tile theme-dark' : 'tile theme-light'">
      <!-- En-tête -->
      <div class="row">
        <div class="col-md-6">
          <div class="toolbar-btn">
            <b [class]="isDark ? 'c-white ff_calibre' : 'c-black ff_calibre ml-2'">
              {{ 'profile.profils' | translate }}
            </b>
          </div>
        </div>
        <div class="col-md-6 ta-right">
          <div class="toolbar-btn">
            <button
              [class]="isDark ? 'btn btn-admin-d btn-sm mr-2' : 'btn btn-admin-w btn-sm mr-2'"
              (click)="showAddForm()"
              type="button"
              data-backdrop="static"
              data-toggle="modal"
              data-target="#addProfilModal">
              <i class="mdi mdi-plus"></i>
              <span>{{ 'profile.addprofil' | translate }}</span>
            </button>
          </div>
        </div>
      </div>

      <!-- Table liste profils -->
      <div class="table-responsive mailbox-messages">
        <p-table
          [value]="profilFiltres()"
          dataKey="id"
          responsiveLayout="scroll"
          class="table table-hover"
          [(selection)]="selectedProfils"
          [paginator]="true"
          [rowHover]="true"
          [rows]="15"
          [showCurrentPageReport]="true"
          currentPageReportTemplate="Affichant {first} à {last} sur {totalRecords} profils">

          <!-- Header -->
          <ng-template pTemplate="header">
            <tr class="tr_boxnone">
              <th></th>
              <th [class]="isDark ? 'th-d th_brnone' : 'th-w th_brnone'">{{ 'profile.numprofil' | translate }}</th>
              <th [class]="isDark ? 'th-d th_brnone' : 'th-w th_brnone'">{{ 'profile.nom' | translate }}</th>
              <th [class]="isDark ? 'th-d th_brnone' : 'th-w th_brnone'">{{ 'profile.description' | translate }}</th>
              <th [class]="isDark ? 'th-d th_brnone' : 'th-w th_brnone'">{{ 'profile.statut' | translate }}</th>
              <th [class]="isDark ? 'th-d th_brnone' : 'th-w th_brnone'">{{ 'profile.bloqued' | translate }}</th>
              <th [class]="isDark ? 'th-d th_brnone' : 'th-w th_brnone'">{{ 'profile.operation' | translate }}</th>
            </tr>
          </ng-template>

          <!-- Body -->
          <ng-template pTemplate="body" let-profil>
            <tr *ngIf="profil">
              <td [class]="isDark ? 'td-d' : 'td-w'">
                <div class="animated-checkbox ml-chec-3">
                  <label>
                    <input type="checkbox" />
                    <span class="label-text"></span>
                  </label>
                </div>
              </td>
              <td [class]="isDark ? 'td-d' : 'td-w'">{{ profil?.id }}</td>
              <td [class]="isDark ? 'td-d' : 'td-w'">{{ profil?.nom }}</td>
              <td [class]="isDark ? 'td-d' : 'td-w'">{{ profil?.description }}</td>
              <td [class]="isDark ? 'td-d' : 'td-w'">
                <span [class]="profil?.statut === 'Activ\u00e9' ? 'text-c-green ft-w-500' : 'text-c-red ft-w-500'">
                  {{ profil?.statut }}
                </span>
              </td>
              <td [class]="isDark ? 'td-d' : 'td-w'">
                <span class="ml-5">
                  <img
                    *ngIf="profil?.statut === 'Activ\u00e9'"
                    title="Bloquer"
                    src="assets/images/switch_on.png"
                    alt="On"
                    style="cursor: pointer; width: 20px"
                    (click)="showConfirmDialog(profil, 'Désactivé')" />
                  <img
                    *ngIf="profil?.statut !== 'Activ\u00e9'"
                    title="Débloquer"
                    src="assets/images/switch_off.png"
                    alt="Off"
                    style="cursor: pointer; width: 30px"
                    (click)="showConfirmDialog(profil, 'Activ\u00e9')" />
                </span>
              </td>
              <td [class]="isDark ? 'td-d' : 'td-w'">
                <div class="d-flex ml-5">
                  <a title="Modifier"
                     *ngIf="profil"
                     (click)="showEditForm(profil)"
                     data-toggle="modal"
                     data-target="#addProfilModal">
                    <div [class]="isDark ? 'opt-edit-d' : 'opt-edit-w'">
                      <i class="mdi mdi-pencil-lock" style="color: #ffd56b; cursor: pointer"></i>
                    </div>
                  </a>
                  <a title="Supprimer"
                     *ngIf="profil"
                     (click)="showDeleteDialog(profil)"
                     data-toggle="modal"
                     data-target="#deleteConfirmModal">
                    <div [class]="isDark ? 'opt-delete-d' : 'opt-delete-w'">
                      <i class="mdi mdi-delete" style="color: #e32131; cursor: pointer"></i>
                    </div>
                  </a>
                </div>
              </td>
            </tr>
          </ng-template>

          <!-- Ligne des droits -->
          <ng-template pTemplate="rowexpansion" let-lineDroit>
            <tr *ngIf="lineDroit">
              <td colspan="7">
                <div class="p-1" style="margin-left: 2%; width: 98%">
                  <p-table [value]="lineDroit?.droits || []" dataKey="id">
                    <ng-template pTemplate="body" let-droit>
                      <tr *ngIf="droit">
                        <td [class]="isDark ? 'td-d' : 'td-w'">{{ droit?.id }}</td>
                        <td [class]="isDark ? 'td-d' : 'td-w'">{{ droit?.nom }}</td>
                        <td [class]="isDark ? 'td-d' : 'td-w'">{{ droit?.description }}</td>
                        <td [class]="isDark ? 'td-d' : 'td-w'">{{ droit?.status }}</td>
                      </tr>
                    </ng-template>
                  </p-table>
                </div>
              </td>
            </tr>
          </ng-template>
        </p-table>
      </div>
    </div>

    <!-- add profil modal -->
    <div class="container demo" *ngIf="currentPage === 'add' && profil">
      <div class="modal right fade" id="addProfilModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel2">
        <div class="modal-dialog" role="document">
          <div class="modal-content" [style]="{'background-color': isDark ? '#1f1f1f' : '#FFFFFF'}">
            <div class="d-flex" style="margin-top: 15px;">
              <button type="button" class="close" data-dismiss="modal" aria-label="Close" #closeAddElementDialog>
                <span aria-hidden="true">
                  <i [class]="isDark ? 'txt-title-d mdi mdi-arrow-left-bold-circle' : 'mdi mdi-arrow-left-bold-circle'"></i>
                </span>
                <span class="form-control-label lab_modal">
                  {{ profil?.id ? 'Modification d\'un profil' : 'Création d\'un nouveau profil' }}
                </span>
              </button>
            </div>
            <div class="modal-body">
              <div class="row mb-l-20 mb-10">
                <div class="col-md-12 col-lg-12 p-right-0">
                  <div [class]="isDark ? 'card cust_card_d' : 'card cust_card_w'">
                    <div class="card-block">
                      <div class="form-group">
                        <label class="form-control-label lab_modal" for="nomProfil">{{ 'profile.nom' | translate }} *</label>
                        <input
                          [class]="isDark ? 'form-control form-control-d' : 'form-control form-control-w'"
                          id="nomProfil"
                          name="nomProfil"
                          type="text"
                          [(ngModel)]="profil.nom"
                          required />
                      </div>

                      <div class="form-group">
                        <label class="form-control-label lab_modal" for="describProfil">{{ 'profile.description' | translate }}</label>
                        <textarea
                          [class]="isDark ? 'form-control form-control-d' : 'form-control form-control-w'"
                          id="describProfil"
                          name="describProfil"
                          [(ngModel)]="profil.description"></textarea>
                      </div>

                      <div align="center">
                        <label class="form-control-label lab_modal">Choix des droits</label>
                        <div class="search-container">
                          <input type="text" name="droitFilter" [(ngModel)]="droitFilter.nom" [placeholder]="'assistante.searchPlaceholder' | translate" />
                          <i class="fa fa-search"></i>
                        </div>
                      </div>
                      <div class="col-lg-12" style="height: 350px; overflow-y: scroll;">
                        <mat-checkbox [(ngModel)]="isSelectedRole" color="primary" name="list_roles" (change)="checkedAllRole()">Sélectionner tous</mat-checkbox>
                        <div *ngFor="let droit of droits | filterBy: droitFilter">
                          <mat-checkbox [(ngModel)]="droit.checked" [name]="'droit-'+droit?.id" color="primary">
                            {{ droit?.nom }}
                          </mat-checkbox>
                        </div>
                      </div>
                      <div class="col-md-12">
                        <button class="btn btn-login btn-block btnn_save" (click)="profil?.id ? Update() : Save()">
                          <img src="assets/images/save.png" alt="" class="icon_save" />
                          {{ 'parametre.save' | translate }}
                        </button>
                      </div>
                      <div class="col-md-12 ta-center">
                        <label class="lab_reset" *ngIf="!profil?.id" (click)="showAddForm()">Réinitialiser</label>
                        <label class="lab_reset" *ngIf="profil?.id" data-dismiss="modal" aria-label="Close">Fermer</label>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    <!-- Modal de confirmation de suppression -->
<div class="modal fade" id="deleteModal" tabindex="-1" aria-labelledby="deleteModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-dialog-centered">
    <div class="modal-content shadow-lg border-0">
      <div class="modal-header bg-danger text-white">
        <h5 class="modal-title" id="deleteModalLabel">
          <i class="mdi mdi-alert"></i> Confirmation de suppression
        </h5>
        <button type="button" class="btn-close btn-close-white" data-bs-dismiss="modal" aria-label="Fermer"></button>
      </div>
      <div class="modal-body text-center">
        <p>Voulez-vous vraiment supprimer le profil <strong>{{ profil?.nom }}</strong> ?</p>
      </div>
      <div class="modal-footer justify-content-center">
        <button type="button" class="btn btn-secondary px-4" data-bs-dismiss="modal">
          <i class="mdi mdi-close"></i> Annuler
        </button>
        <button type="button" class="btn btn-danger px-4" (click)="DeleteProfile()">
          <i class="mdi mdi-delete"></i> Supprimer
        </button>
      </div>
    </div>
  </div>
</div>
  </div>
</div>

   <!-- Modal confim bloque -->
    <div class="modal fade text-xs-left" id="elementConfirmModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel2" aria-hidden="true">
      <div class="modal-sm modal-dialog " role="document">
        <div class="modal-content" style="width: 340px;">
          <div class="modal-header">
            <button style="display: none;" type="button" #openConfirmDialog class="btn btn-outline-primary" data-backdrop="static" data-toggle="modal" data-target="#elementConfirmModal" data-backdrop="true">
            </button>
            <h4 class="modal-title" id="myModalLabel2">
              <img src="assets/images/close_c.png" alt="" class="im_confirm">
            </h4>
            <img src="assets/images/close_x.png" alt="" class="cursor-p" data-dismiss="modal">
          </div>
          <div class="modal-body">
            <label class="txt_confirm">
              Bonjour, vous vous appretez à bloquer le {{profil?.nom}}
            </label><br/>
            <label class="desc_confirm">etes vous sûr de bloquer ce Service ?</label> <br>

            <button class="button_op">
                <span class="buttxt_op">Operations</span>
            </button>
